import React, { useState } from 'react';
import ReCAPTCHA from 'react-google-recaptcha';
import './styles/EnquireButton.css';

const EnquireButton = () => {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [formData, setFormData] = useState({
        name: '',
        contact: '',
        email: '',
        message: '',
        captcha: '',
    });

    const toggleModal = () => {
        setIsModalOpen(!isModalOpen);
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleCaptchaChange = (value) => {
        setFormData({
            ...formData,
            captcha: value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Handle form submission logic here
        console.log('Form submitted', formData);
        toggleModal();
    };

    const isFormValid = formData.name && formData.contact && formData.email && formData.message && formData.captcha;

    return (
        <>
            <a href="#enquire" className="enquire-button" onClick={toggleModal}>Enquire Now</a>
            {isModalOpen && (
                <div className="modal-overlay">
                    <div className="modal-content">
                        <button className="close-button" onClick={toggleModal}>X</button>
                        <form onSubmit={handleSubmit}>
                            <h2>Enquire Now</h2>
                            <label>
                                Name:
                                <input type="text" name="name" value={formData.name} onChange={handleChange} required />
                            </label>
                            <label>
                                Contact Number:
                                <input type="text" name="contact" value={formData.contact} onChange={handleChange} required />
                            </label>
                            <label>
                                Email:
                                <input type="email" name="email" value={formData.email} onChange={handleChange} required />
                            </label>
                            <label>
                                Message:
                                <textarea name="message" value={formData.message} onChange={handleChange} required></textarea>
                            </label>
                            <ReCAPTCHA
                                sitekey="YOUR_RECAPTCHA_SITE_KEY"
                                onChange={handleCaptchaChange}
                            />
                            <button type="submit" disabled={!isFormValid}>Submit</button>
                        </form>
                    </div>
                </div>
            )}
        </>
    );
};

export default EnquireButton;
