const NavBar = () => {
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [isModalOpen, setIsModalOpen] = useState(false);

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    const toggleModal = () => {
        setIsModalOpen(!isModalOpen);
    };

    return (
        <>
            <nav className="navbar">
                <div className="logo">
                    <img src={logo} alt="Logo" />
                </div>
                <div className={`nav-buttons ${isMenuOpen ? 'active' : ''}`}>
                    <a href="#home">Home</a>
                    <a href="#about">About</a>
                    <a href="#services">Services</a>
                    <a href="#contact">Contact</a>
                    <a style={{ backgroundColor: "green", color: "white" }} href="#appointment">Appointment</a>
                    <button className="enquire-button" onClick={toggleModal}>Enquire Now</button>
                </div>
                <div className={`menu-icon ${isMenuOpen ? 'open' : ''}`} onClick={toggleMenu}>
                    {isMenuOpen ? (
                        <div className="close-button">Close</div>
                    ) : (
                        <>
                            <div className="bar"></div>
                            <div className="bar"></div>
                            <div className="bar"></div>
                        </>
                    )}
                </div>
            </nav>
            {isModalOpen && <EnquireModal toggleModal={toggleModal} />}
        </>
    );
};

export default NavBar;
