import React, { useState } from 'react';
import { Navbar, Nav, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { FaSun, FaMoon } from 'react-icons/fa';
import './assests/styles/Header.css';
import { ReactComponent as Logo } from './assests/images/NIMS.svg';

const Header = () => {
  const [theme, setTheme] = useState('light');

  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
    document.body.className = newTheme;
  };

  return (
    <Navbar bg={theme} variant={theme} expand="lg" className="header-navbar">
      <Navbar.Brand as={Link} to="/">
      
      {/* <Navbar.Toggle aria-controls="basic-navbar-nav" /> */}
      <Logo className="hospital-logo" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="ml-auto">
          <Button as={Link} to="/" variant="outline-primary" className="nav-button">Home</Button>
          <Button as={Link} to="/about" variant="outline-primary" className="nav-button">About</Button>
          <Button as={Link} to="/services" variant="outline-primary" className="nav-button">Services</Button>
          <Button as={Link} to="/contact" variant="outline-primary" className="nav-button">Contact</Button>
          <Button as={Link} to="/appointments" variant="outline-primary" className="nav-button">Appointments</Button>
        </Nav>
        <Button onClick={toggleTheme} variant="outline-secondary" className="theme-toggle">
          {theme === 'light' ? <FaMoon /> : <FaSun />}
        </Button>
      </Navbar.Collapse>
      </Navbar.Brand>
    </Navbar>
  );
};

export default Header;
