import React, { useState } from 'react';
import './styles/EnquireModal.css';

const EnquireModal = ({ toggleModal }) => {
    const [formData, setFormData] = useState({
        name: '',
        contact: '',
        email: '',
        message: '',
        isHuman: false,
    });

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData({
            ...formData,
            [name]: type === 'checkbox' ? checked : value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Handle form submission logic here
        console.log('Form submitted', formData);
        toggleModal();
    };

    const isFormValid = formData.isHuman && formData.name && formData.contact && formData.email && formData.message;

    return (
        <div className="modal-overlay">
            <div className="modal-content">
                <button className="close-button" onClick={toggleModal}>X</button>
                <form onSubmit={handleSubmit}>
                    <h2>Enquire Now</h2>
                    <label>
                        Name:
                        <input type="text" name="name" value={formData.name} onChange={handleChange} required />
                    </label>
                    <label>
                        Contact Number:
                        <input type="text" name="contact" value={formData.contact} onChange={handleChange} required />
                    </label>
                    <label>
                        Email:
                        <input type="email" name="email" value={formData.email} onChange={handleChange} required />
                    </label>
                    <label>
                        Message:
                        <textarea name="message" value={formData.message} onChange={handleChange} required></textarea>
                    </label>
                    <label>
                        <input type="checkbox" name="isHuman" checked={formData.isHuman} onChange={handleChange} />
                        I am not a robot
                    </label>
                    <button type="submit" disabled={!isFormValid}>Submit</button>
                </form>
            </div>
        </div>
    );
};

export default EnquireModal;
